mapper.select:
select u.name as owner,a.name,a.start_date,a.end_date, a.id, a.cost, a.description, a.create_time, a.create_by, a.edit_time, a.edit_by from tbl_activity a left join tbl_user u on a.owner = u.id
and a.name like '%' #{activityName} '%'
and u.name like '%' #{ownerName} '%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.start_date limit #{pageNo},#{pageSize}

select count(a.id) from tbl_activity a left join tbl_user u on a.owner = u.id
and a.name like '%' #{activityName} '%'
and u.name like '%' #{ownerName} '%'
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
